import React from 'react'
import {Button, Container} from 'react-bootstrap'

class StartScreen extends React.Component{
  clickHandler=()=>{
    let{startGame}=this.props
    startGame()
  }
  render(){
    return(
      <Container id="start-screen">
      <div>
     <h1 id="hero-title" className="">BATTLESHIP</h1>
      </div>
      <Button id="start-game-btn" type="button" className="btn btn-danger" onClick={this.clickHandler}>
      Start Game
      </Button>
      </Container>
    )
  }
}


export default StartScreen
