import React from 'react'

class SingleSquare extends React.Component{
  constructor(props){
    super(props)
    this.state={
      style:{background: ""},
      hover: false,
      clicked: false,

    }
  }
  clickHandler=()=>{
  let {style, clicked}=this.state
  let{placeBoat, index, value, placementMode, battleMode, fire}=this.props
      if(placementMode){
      placeBoat(index)
    }else if(battleMode){
      fire(index)
    }
  }

  hoverHandler=()=>{
  let {onHoverHighlight, index,}=this.props
    if(placementMode){
      onHoverHighlight(index)
    }else if(battleMode){
      document.getElementById(index).innerHTML ="X"
    }
  }
  render(){
    let{style}=this.state
    let{value,index}=this.props
    return(
      <div style={style} className="single-square" onMouseOver={this.hoverHandler}  onClick={this.clickHandler} id={index}>
      {value}
      </div>
    );
  }
}
export default SingleSquare
