import React from 'react';
import{Button}from 'react-bootstrap'
import './App.css';
import Board from './board.js'
import HowtoPlay from './howtoplay.js'
import StartScreen from './start_screen.js'

class App extends React.Component{
  constructor(props){
    super(props)
    this.state={
      startScreen: true,
      mainMenu: false,
      gameStart: false,
    }
  }
  enterMainMenu=()=>{
    let {mainMenu}=this.state
    this.setState({mainMenu: true})
  }
  startGame=()=>{
    let {gameStart}=this.state
    this.setState({gameStart: true})
  }
  render(){
    let {gameStart, mainMenu, startScreen}=this.state
    let{startGame, enterMainMenu}=this
    return (
      <div className="App">
      {startScreen &&<StartScreen startGame={startGame}/>}
        {gameStart && <Board />}
        {!mainMenu&& <HowtoPlay />}
      </div>
    );
  }
}

export default App;
