import React from 'react';
import{Button}from 'react-bootstrap'
import './App.css';
import Board from './board.js'
import StartScreen from './start_screen.js'

class App extends React.Component{
  constructor(props){
    super(props)
    this.state={
      gameStart: false,
    }
  }
  startGame=()=>{
    console.log("startGame ran");
    let {gameStart}=this.state
    this.setState({gameStart: true})
  }
  render(){
    let {gameStart}=this.state
    return (
      <div className="App">
      <StartScreen gameStart={gameStart}/>
        {gameStart && <Board />}
      </div>
    );
  }
}

export default App;
