import React from 'react'

class SingleSquare extends React.Component{
  constructor(props){
    super(props)
    this.state={
      style:{background: ""},
      hover: false,

    }
  }
  clickHandler=()=>{
  let {style}=this.state
  this.setState({style: {background: "blue"}})
  }
  hoverHandler=()=>{
  let {onHoverHighlight, index}=this.props
  onHoverHighlight(index)
  }
  render(){
    let{style}=this.state
    let{value}=this.props
    return(
      <div style={style} className="shadow-inset-center" onMouseOver={this.hoverHandler} onClick={this.clickHandler} id="single-square">
      <h3>{value}</h3>
      </div>
    );
  }
}
export default SingleSquare
